mapper.insert:
INSERT INTO TB_MATCHING (
    USER_ID, ERR_NUM
)
VALUES (
    #{user_id}, #{err_num}
)

mapper.select:
 
SELECT  ERR_NUM
FROM TB_MATCHING
WHERE USER_ID = #{user_id}

 
SELECT  ERR_NUM
FROM TB_MATCHING
WHERE USER_ID = #{user_id} AND ERR_NUM = #{err_num}

SELECT USER_ID
FROM TB_MATCHING
WHERE ERR_NUM = #{selectedNum}

SELECT MATCH_YN
FROM TB_MATCHING
WHERE USER_ID = #{user_id} AND ERR_NUM = #{err_num}

mapper.update:
UPDATE TB_Matching
SET MATCH_YN = 'R'
WHERE USER_ID = #{user_id} AND ERR_NUM = #{err_num}

UPDATE TB_Matching
