mapper.insert:
INSERT INTO TB_USER (
    USER_ID, USER_PW, USER_NAME, USER_BIRTHDATE, USER_GENDER, USER_NICK, USER_JUMIN, USER_PHONE, USER_ADDR1
)
VALUES (
    #{user_id}, #{user_pw}, #{user_name}, #{user_birthdate}, #{user_gender},
    #{user_nick}, #{user_jumin}, #{user_phone}, #{user_addr1}
)

mapper.select:
SELECT USER_ID, USER_PW
FROM TB_USER
WHERE USER_ID = #{user_id} AND USER_PW = #{user_pw}

mapper.update:
UPDATE TB_USER
SET USER_PW = #{user_pw}, USER_NAME = #{user_name} , USER_NICK = #{user_nick}, USER_PHONE = #{user_phone} , USER_ADDR1 = #{user_addr1}
WHERE USER_ID = #{user_id}

mapper.select:
SELECT USER_ID, USER_PW
FROM TB_USER 
WHERE USER_NAME = #{user_name}

SELECT USER_NAME, USER_PW, USER_JUMIN
FROM TB_USER 
WHERE USER_NAME = #{user_name} AND USER_JUMIN = #{user_jumin}
