mapper.insert:
INSERT INTO TB_POST (
POST_TITLE, POST_CONTENT, USER_ID
)
VALUES (
#{post_title} , #{post_content}, #{user_id}
)

mapper.select:
SELECT POST_NUM ,POST_TITLE, POST_CONTENT, POSTED_AT, POST_LIKES, POST_VIEWS, USER_ID
FROM TB_POST
WHERE POST_NUM = #{post_num}

mapper.update:
UPDATE TB_POST
SET POST_VIEWS = POST_VIEWS + 1
WHERE POST_NUM = #{post_num}
